<?phpnamespace App\Domain\Project;use function App\addLog;use App\Common\CommonDomain;use App\Common\Exception\WrongRequestException;use function App\nowTime;/** * 任务类型模板类 * * @author lws */class TaskTypeTemplate extends CommonDomain{    private static $Model = null;    public function __construct()    {        if (self::$Model == null) {            self::$Model = new \App\Model\Project\TaskTypeTemplate();        }    }    public function getList($param)    {        if (!is_array($param)) {            $param = get_object_vars($param);        }        if (isset($param['keyWord'])) {            $param['where']["name LIKE ? "] = array("%" . $param['keyWord'] . "%");        }        $param['where']['project_type_id'] = $param['project_type_id'];        $list = self::$Model->getList($param);        return $list;    }    public function getInfo($where,$field = '*')    {        return self::$Model->getInfo($where,$field);    }    /** 删除任务类型模板     * @param $ids id列表 如1,2,3     * @return int     */    public function delTaskTypeTemplate($ids)    {        $result = self::$Model->delItems($ids);        if ($result) {            $ids = json_encode($ids);            addLog("删除项目任务类型模板，编号：$ids");        }        return $result == true ? 0 : 1;    }    /**     *  新增任务类型模板     * @param $data     * @throws WrongRequestException     */    public function addTaskTypeTemplate($data)    {        $data['create_time'] = nowTime();        $id = self::$Model->insert($data);        if ($id === false) {            throw new WrongRequestException('新增失败', 8);        }        addLog('新增任务类型模板，编号：' . $id);    }    public function editProjectTaskTypeTemplate($id,$data)    {        $result = self::$Model->update($id,$data);        if ($result === false) {            throw new WrongRequestException('保存失败', 6);        }        addLog('修改任务类型模板，模板ID：' . $id);    }}